@Slf4j
@Component
public class ApiLogFilter implements GlobalFilter, Ordered {

    private static final String START_TIME = "startTime";

    private static final String X_REAL_IP = "X-Real-IP";

    private final DataBuffer emptyBuffer = new NettyDataBufferFactory(ByteBufAllocator.DEFAULT).allocateBuffer(0);

    @Override
    public Mono<Void> filter(ServerWebExchange exchange, GatewayFilterChain chain) {

        //è¯·æ±‚çš„åŸºæœ¬ä¿¡æ¯
        List<String> ips = exchange.getRequest().getHeaders().get(X_REAL_IP);
        String ip = ips != null ? ips.get(0) : null;
        String method = exchange.getRequest().getMethodValue();
        String path = exchange.getRequest().getURI().getPath();

        exchange.getAttributes().put(START_TIME, System.currentTimeMillis());
        ServerHttpRequest request = exchange.getRequest();

        if (path.contains("api-docs")) {
            log.warn("{}æ­£åœ¨è¯·æ±‚æ¥å£æ–‡æ¡£{}", ip, path);
            return chain.filter(exchange.mutate().request(request).build());
        }

        //æ‰“å°è¯·æ±‚å¼€å§‹æ—¥å¿—
        log.info("è¯·æ±‚å¼€å§‹:\n\næ–¹æ³•:{}\nHost:{}\nip:{}\nè¯·æ±‚æ¥å£:{}\n", method, exchange.getRequest().getURI().getHost(), ip,
            path);

        //æ‰“å°headerå¤´
        log.info("è¯·æ±‚å¤´:\n\n{}\n", JSONUtil.toJsonStr(exchange.getRequest().getHeaders()));

        //åŒ…è£…response,åœ¨ä»»æ„åœ°æ–¹è¿”å›æ‰“å°ç»“æŸä¿¡æ¯ç­‰
        ServerHttpResponse originalResponse = exchange.getResponse();
        DataBufferFactory bufferFactory = originalResponse.bufferFactory();
        ServerHttpResponseDecorator decoratedResponse = new ServerHttpResponseDecorator(originalResponse) {
            @Override
            @NotNull
            public Mono<Void> writeWith(@NotNull Publisher<? extends DataBuffer> body) {
                Long startTime = exchange.getAttribute(START_TIME);
                if (startTime != null) {
                    Long executeTime = (System.currentTimeMillis() - startTime);
                    int code = 500;
                    if (exchange.getResponse().getStatusCode() != null) {
                        code = exchange.getResponse().getStatusCode().value();
                    }
                    log.info("è¯·æ±‚ç»“æŸä¿¡æ¯:\n\nå“åº”çŠ¶æ€ç ï¼š{}\nè¯·æ±‚è€—æ—¶ï¼š{}ms\n", code, executeTime);
                }

                if (body instanceof Flux) {
                    Flux<? extends DataBuffer> fluxBody = Flux.from(body);
                    return super.writeWith(fluxBody.map(dataBuffer -> {
                        byte[] content = new byte[dataBuffer.readableByteCount()];
                        dataBuffer.read(content);
                        DataBufferUtils.release(dataBuffer);
                        String data = new String(content, StandardCharsets.UTF_8);//data
                        log.info("è¯·æ±‚ç»“æŸè¿”å›: \n\n{}\n", data);
                        return bufferFactory.wrap(content);
                    }));
                } else {
                    log.error("<--- {} å“åº”codeå¼‚å¸¸", getStatusCode());
                }
                MDC.clear();
                return super.writeWith(body);
            }
        };
        //æ‰“å°è¯·æ±‚å‚æ•°
        MultiValueMap<String, String> queryParams = request.getQueryParams();
        if (!queryParams.isEmpty()) {
            log.info("è¯·æ±‚å‚æ•°(url)ï¼š\n\n{}\n", queryParams);
        }

        if (HttpMethod.POST.name().equals(method)) {
            return DataBufferUtils.join(request.getBody()).defaultIfEmpty(emptyBuffer).flatMap(dataBuffer -> {
                byte[] bytes = new byte[dataBuffer.readableByteCount()];
                if (PrimitiveArrayUtil.isNotEmpty(bytes)) {
                    dataBuffer.read(bytes);
                    String bodyString = new String(bytes, StandardCharsets.UTF_8);
                    try {
                        bodyString = UriUtils.decode(bodyString, "UTF-8");
                    } catch (Exception e) {
                        log.error("è¯·æ±‚å‚æ•°ç¼–ç é”™è¯¯æœ‰ç‰¹æ®Šç¬¦å·", e);
                    }
                    log.info("è¯·æ±‚å‚æ•°(form)ï¼š\n\n{}\n", bodyString);
                    exchange.getAttributes().put("POST_BODY", bodyString);
                    DataBufferUtils.release(dataBuffer);
                    Flux<DataBuffer> cachedFlux = Flux.defer(() -> {
                        DataBuffer buffer = exchange.getResponse().bufferFactory().wrap(bytes);
                        return Mono.just(buffer);
                    });
                    ServerHttpRequest mutatedRequest = new ServerHttpRequestDecorator(request) {
                        @Override
                        public Flux<DataBuffer> getBody() {
                            return cachedFlux;
                        }
                    };
                    return chain.filter(exchange.mutate().request(mutatedRequest).response(decoratedResponse).build());
                } else {
                    return chain.filter(exchange.mutate().request(request).response(decoratedResponse).build());
                }
            });
        }
        return chain.filter(exchange.mutate().request(request).response(decoratedResponse).build());
    }

    @Override
    public int getOrder() {
        return Ordered.HIGHEST_PRECEDENCE;
    }

}

